import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for thread feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });
    await page.waitForSelector('.thread-username', { timeout: 3000 });

    // Extract all visible usernames from thread feed
    const usernames = await page.$$eval('.thread-username', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_usernames_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_usernames_extracted.png'));

    return {
      success: true,
      action: "Collected all visible usernames from thread feed",
      extractedData: { usernames },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to collect usernames from thread feed",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

