import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Find all visible like buttons using multiple selector strategies
    const likeButtons = await page.$$eval('.thread-action[data-action="like"]', 
      buttons => buttons.filter(btn => btn.offsetParent !== null)
    );

    // Click all visible like buttons in rapid succession
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    }

    // Take screenshot after clicking like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    // Extract data to verify completion
    const likedThreads = await page.$$eval('.thread-item', threads => 
      threads.map(thread => ({
        content: thread.querySelector('.thread-content')?.textContent?.trim() || '',
        isLiked: thread.querySelector('.thread-action[data-action="like"]')?.classList.contains('active') || false
      }))
    );

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        totalThreads: likedThreads.length,
        likedThreads: likedThreads.filter(t => t.isLiked).length,
        threads: likedThreads
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
