import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the emoji button
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');
    await page.waitForTimeout(500); // Wait for emoji picker animation

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_opened.png'));

    // Wait for emoji items to load and select a smile emoji
    await page.waitForSelector('.emoji-item');
    const emojiItems = await page.$$('.emoji-item');

    // Find a smile emoji (typically first few items in most emoji pickers)
    if (emojiItems.length > 0) {
      const firstEmoji = await emojiItems[0].textContent();
      await emojiItems[0].click();

      // Verify emoji appears in thread text
      await page.waitForTimeout(300); // Wait for emoji insertion
      const threadText = await page.$eval('#thread-text', el => el.value);

      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_added.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_emoji_added.png'));

      return {
        success: threadText.includes(firstEmoji),
        action: "Clicked emoji button, selected smile emoji, and verified it appeared in thread text",
        extractedData: {
          selectedEmoji: firstEmoji,
          threadTextContent: threadText,
          emojiFound: threadText.includes(firstEmoji)
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('No emoji items found in picker');
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

