    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the emoji button using multiple selector strategies
    const emojiButton = await page.waitForSelector('#add-emoji');
    await emojiButton.click();
