    await page.waitForSelector('.emoji-item');

    // Get all emoji items and select the first smile-like emoji
    const emojiItems = await page.$$('.emoji-item');
    const selectedEmoji = await page.evaluate(el => el.textContent, emojiItems[0]);

    // Click the first emoji (assuming it's a smile emoji)
    await emojiItems[0].click();
    await page.waitForTimeout(500); // Wait for emoji to be inserted

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_selected.png'));

    // Verify the emoji appears in the thread text
    const threadText = await page.$eval('#thread-text', el => el.value);
    const emojiInText = threadText.includes(selectedEmoji);

    if (!emojiInText) {
      throw new Error('Selected emoji did not appear in the thread text');
    }

    return {
      success: true,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
      extractedData: {
        selectedEmoji: selectedEmoji,
        threadText: threadText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
    screenshots.push(path.join(screenshotsDir, 'step_error.png'));

    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

