import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and ensure reply button is available
    await page.waitForSelector('.thread-action[data-action="reply"]', { timeout: 5000 });

    // Perform 10 rapid open/close cycles of reply modal
    for (let i = 0; i < 10; i++) {
      // Open reply modal
      await page.click('.thread-action[data-action="reply"]');

      // Wait for modal to open
      await page.waitForSelector('#close-reply-modal', { state: 'visible', timeout: 2000 });

      // Take screenshot of open modal on first iteration
      if (i === 0) {
        await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
        screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));
      }

      // Close modal
      await page.click('#close-reply-modal');

      // Wait for modal to close
      await page.waitForSelector('#close-reply-modal', { state: 'hidden', timeout: 2000 });
    }

    // Take final screenshot after all operations
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times in rapid succession",
      extractedData: {
        cyclesCompleted: 10,
        modalSelector: '.thread-action[data-action="reply"]',
        closeSelector: '#close-reply-modal'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

