import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.threads-feed', { timeout: 5000 });

    // Get all thread elements
    const threads = await page.$$('#threads-feed > div');

    // Extract thread data
    const extractedData = [];

    for (const thread of threads) {
      try {
        // Extract text content
        const textElement = await thread.$('.thread-text');
        const text = textElement ? await textElement.textContent() : 'No text content';

        // Extract time
        const timeElement = await thread.$('.thread-time');
        const time = timeElement ? await timeElement.textContent() : 'No time data';

        // Extract actions
        const actionElements = await thread.$$('.thread-action span');
        const actions = [];
        for (const action of actionElements) {
          const actionText = await action.textContent();
          if (actionText) actions.push(actionText);
        }

        extractedData.push({
          text: text.trim(),
          time: time.trim(),
          actions: actions
        });
      } catch (error) {
        console.log('Error extracting thread data:', error.message);
      }
    }

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the feed",
      extractedData: {
        threads: extractedData,
        count: extractedData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract thread content and metadata",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
