import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on search navigation item
    await page.waitForSelector('.nav-item[data-view="search"]');
    await page.click('.nav-item[data-view="search"]');

    // Wait for search view to be active
    await page.waitForSelector('#search-view.active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type 'jun' in search input
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'jun');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_typed_jun.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_typed_jun.png'));

    // Switch to People tab
    await page.waitForSelector('.tab-btn[data-tab="people"]');
    await page.click('.tab-btn[data-tab="people"]');

    // Wait for tab to activate
    await page.waitForTimeout(500); // Allow UI to update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Extract verification data
    const searchValue = await page.$eval('#search-input', el => el.value);
    const activeTab = await page.$eval('.tab-btn.active', el => el.dataset.tab);

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData: {
        searchInputValue: searchValue,
        activeTab: activeTab
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

