import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item using data attribute
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');
    await page.waitForSelector('#search-view.active');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type 'jun' in search input
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'jun');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_typed.png'));

    // Switch to People tab using data-tab attribute
    await page.waitForSelector('.tab-btn[data-tab="people"]');
    await page.click('.tab-btn[data-tab="people"]');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Extract search results to verify completion
    const searchResults = await page.$$eval('.search-result-item', items => 
      items.map(item => ({
        username: item.querySelector('.username')?.textContent || '',
        handle: item.querySelector('.user-handle')?.textContent || ''
      }))
    );

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData: {
        searchQuery: 'jun',
        resultsCount: searchResults.length,
        searchResults: searchResults
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
