import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for right sidebar to load
    await page.waitForSelector('.right-sidebar', { timeout: 5000 });

    // Wait for suggested accounts section to load
    await page.waitForSelector('.suggested-accounts', { timeout: 5000 });

    // Wait for first account item to be visible
    await page.waitForSelector('.account-item:first-child', { timeout: 5000 });

    // Extract account information before clicking
    const accountInfo = await page.$eval('.account-item:first-child', el => ({
      username: el.querySelector('.username')?.textContent || 'N/A',
      handle: el.querySelector('.user-handle')?.textContent || 'N/A'
    }));

    // Take screenshot before clicking follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_follow.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_follow.png'));

    // Click the follow button on first suggested account
    await page.click('.account-item:first-child .follow-btn');

    // Wait for follow state to update
    await page.waitForTimeout(1000);

    // Take screenshot after clicking follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_follow.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_follow.png'));

    // Verify the button state changed
    const buttonText = await page.$eval('.account-item:first-child .follow-btn', 
      btn => btn.textContent.trim());

    return {
      success: true,
      action: "Clicked Follow button on first suggested account",
      extractedData: {
        account: accountInfo,
        buttonState: buttonText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

