import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the right sidebar to load with suggested accounts
    await page.waitForSelector('.right-sidebar', { timeout: 10000 });

    // Take screenshot of right sidebar
    const rightSidebar = await page.$('.right-sidebar');
    await rightSidebar.screenshot({ path: path.join(screenshotsDir, 'step_2_right_sidebar.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_right_sidebar.png'));

    // Wait for the first suggested account and its follow button
    await page.waitForSelector('.account-item:first-child .follow-btn', { timeout: 5000 });

    // Get account information before clicking
    const accountInfo = await page.evaluate(() => {
      const accountItem = document.querySelector('.account-item:first-child');
      return {
        username: accountItem.querySelector('.username')?.textContent,
        handle: accountItem.querySelector('.user-handle')?.textContent,
        buttonText: accountItem.querySelector('.follow-btn')?.textContent
      };
    });

    // Click the follow button
    await page.click('.account-item:first-child .follow-btn');

    // Wait for button state change (optional animation/state change)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked Follow button on the first suggested account in the right sidebar",
      extractedData: {
        accountUsername: accountInfo.username,
        accountHandle: accountInfo.handle,
        initialButtonText: accountInfo.buttonText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
