import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Search view
    await page.waitForSelector('.nav-item[data-view="search"]');
    await page.click('.nav-item[data-view="search"]');

    // Wait for search view to load
    await page.waitForSelector('#search-view.active', { timeout: 5000 });

    // Take screenshot of search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Click on Hashtags tab
    await page.waitForSelector('.tab-btn[data-tab="hashtags"]');
    await page.click('.tab-btn[data-tab="hashtags"]');

    // Wait for tab to become active
    await page.waitForTimeout(500); // Brief pause for UI update

    // Take screenshot after clicking Hashtags tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_tab.png'));

    // Verify the tab is active by checking for active class
    const isActive = await page.$eval('.tab-btn[data-tab="hashtags"]', el => 
      el.classList.contains('active')
    );

    return {
      success: true,
      action: "Successfully clicked on 'Hashtags' tab to switch search category",
      extractedData: {
        hashtagsTabActive: isActive,
        tabText: await page.$eval('.tab-btn[data-tab="hashtags"]', el => el.textContent.trim())
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

