import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on the thread text input
    await page.waitForSelector('#thread-text');
    await page.focus('#thread-text');

    // Type 'Hello World!' in the thread input
    await page.fill('#thread-text', 'Hello World!');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed_text.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed_text.png'));

    // Click the Post button
    await page.waitForSelector('#post-thread');
    await page.click('#post-thread');

    // Wait for potential UI updates and take final screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_post_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_post_clicked.png'));

    // Verify the text was cleared after posting
    const currentText = await page.$eval('#thread-text', el => el.value);
    const postButtonState = await page.$eval('#post-thread', el => el.disabled);

    return {
      success: true,
      action: "Typed 'Hello World!' in thread input and clicked Post button",
      extractedData: {
        finalTextContent: currentText,
        postButtonDisabled: postButtonState
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

