import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread textarea to be visible using multiple selector strategies
    const threadTextarea = await page.waitForSelector('#thread-text', { state: 'visible' });

    // Type the text using a more reliable method with delays between characters
    await threadTextarea.type('Hello World!', { delay: 50 });

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Wait for and click the Post button using multiple selector strategies
    const postButton = await page.waitForSelector('#post-thread', { state: 'visible' });
    await postButton.click();

    // Wait for any potential UI changes after posting
    await page.waitForTimeout(1000);

    // Take final screenshot after posting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_posting.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_posting.png'));

    // Extract verification data
    const extractedText = await page.$eval('#thread-text', el => el.value);
    const postButtonState = await page.$eval('#post-thread', el => el.disabled);

    return {
      success: true,
      action: "Typed 'Hello World!' in thread input and clicked Post button",
      extractedData: {
        textEntered: extractedText,
        postButtonDisabled: postButtonState
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}
