import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Switch to search view
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');
    await page.waitForSelector('#search-view.active');

    // Generate 20 different search queries
    const searchQueries = [
      'technology', 'programming', 'javascript', 'react', 'nodejs',
      'web development', 'design', 'artificial intelligence', 'machine learning',
      'data science', 'cybersecurity', 'cloud computing', 'blockchain',
      'startup', 'entrepreneurship', 'digital marketing', 'ux design',
      'mobile development', 'open source', 'software engineering'
    ];

    // Perform 20 rapid successive searches
    for (let i = 0; i < searchQueries.length; i++) {
      const query = searchQueries[i];

      // Clear and type search query
      await page.waitForSelector('#search-input');
      await page.fill('#search-input', '');
      await page.type('#search-input', query, { delay: 10 });

      // Wait briefly between searches to simulate rapid succession
      await page.waitForTimeout(100);

      // Take screenshot every 5 searches
      if ((i + 1) % 5 === 0) {
        await page.screenshot({ 
          path: path.join(screenshotsDir, `step_2_search_${i + 1}.png`) 
        });
        screenshots.push(path.join(screenshotsDir, `step_2_search_${i + 1}.png`));
      }
    }

    // Final screenshot after all searches
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Performed 20 rapid successive search queries to stress the search system",
      extractedData: {
        searchesPerformed: 20,
        searchQueries: searchQueries
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search stress test",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

