    await page.waitForSelector('.emoji-item');
    const emojiItems = await page.$$('.emoji-item');

    // Find a smile emoji (using common smile emojis)
    let smileEmoji = null;
    for (const emoji of emojiItems) {
      const emojiText = await emoji.textContent();
      if (['ðŸ˜€', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜†', 'ðŸ˜Š', 'ðŸ™‚'].includes(emojiText)) {
        smileEmoji = emojiText;
        await emoji.click();
        break;
      }
    }

    if (!smileEmoji) {
      // If no specific smile emoji found, click the first available emoji
      await emojiItems[0].click();
      smileEmoji = await emojiItems[0].textContent();
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
