    const smileEmoji = await page.waitForSelector('xpath=//div[contains(@class, "emoji-item") and contains(text(), "ðŸ˜Š")] | //div[contains(@class, "emoji-item") and contains(text(), "ðŸ˜€")] | //div[contains(@class, "emoji-item") and contains(text(), "ðŸ™‚")]');
    await smileEmoji.click();

