import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and ensure we're on the home view with threads
    await page.waitForSelector('#home-view.active');
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Extract thread data
    const threadsData = await page.$$eval('.thread-item', (threads) => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.innerText || '';
        const time = thread.querySelector('.thread-time')?.innerText || '';
        const actions = Array.from(thread.querySelectorAll('.thread-action span'))
          .map(span => span.innerText)
          .filter(text => text.trim() !== '');

        return {
          text,
          time,
          actions
        };
      });
    });

    // Take screenshot of threads
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: {
        threads: threadsData,
        totalThreads: threadsData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

