import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');
    await page.waitForSelector('.thread-text', { timeout: 5000 });

    // Extract all thread data
    const threadsData = await page.$$eval('#threads-feed > div', threads => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent || '';
        const time = thread.querySelector('.thread-time')?.textContent || '';

        const actionSpans = thread.querySelectorAll('.thread-action span');
        const actions = Array.from(actionSpans).map(span => span.textContent);

        return {
          text,
          time,
          actions
        };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the feed",
      extractedData: {
        threadCount: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract thread content and metadata",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
