import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Wait for first thread item to be present
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Use multiple selector strategies to find the reply button
    const replyButton = await page.$('.thread-item:first-child .thread-action[data-action="reply"]');

    if (!replyButton) {
      throw new Error('Reply button not found in first thread');
    }

    // Click the reply button
    await replyButton.click();

    // Wait for reply modal to appear - using multiple indicators
    await Promise.race([
      page.waitForSelector('#reply-image-input', { timeout: 3000 }),
      page.waitForSelector('.modal-content', { timeout: 3000 }),
      page.waitForSelector('textarea[placeholder*="reply"]', { timeout: 3000 })
    ]);

    // Take screenshot after opening modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reply_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reply_modal_open.png'));

    // Extract data to verify modal opened
    const modalVisible = await page.$eval('.modal-content', el => {
      return window.getComputedStyle(el).display !== 'none';
    }).catch(() => false);

    const replyInputVisible = await page.$eval('#reply-image-input', el => {
      return window.getComputedStyle(el).display !== 'none';
    }).catch(() => false);

    return {
      success: true,
      action: "Clicked reply button on first thread and opened reply modal",
      extractedData: {
        modalVisible,
        replyInputVisible,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

