    const appearanceTab = await page.waitForSelector('.tab-btn[data-tab="appearance"]', { state: 'visible' });
    await appearanceTab.click();
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
