import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to search view first
    await page.waitForSelector('.nav-item[data-view="search"]');
    await page.click('.nav-item[data-view="search"]');
    await page.waitForSelector('#search-view.active');

    // Wait for search tabs to load and click Hashtags tab
    await page.waitForSelector('button.tab-btn[data-tab="hashtags"]');
    await page.click('button.tab-btn[data-tab="hashtags"]');

    // Wait for tab activation and take screenshot
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hashtags_tab.png'));

    // Verify the tab was successfully clicked by checking active state
    const isHashtagsActive = await page.$eval('button.tab-btn[data-tab="hashtags"]', 
      el => el.classList.contains('active'));

    // Extract tab information for verification
    const activeTabData = await page.$eval('.tab-btn.active', el => ({
      text: el.textContent.trim(),
      dataTab: el.dataset.tab
    }));

    return {
      success: true,
      action: "Clicked on 'Hashtags' tab to switch search category",
      extractedData: {
        hashtagsTabActive: isHashtagsActive,
        activeTab: activeTabData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
