import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to load and locate Settings button
    await page.waitForSelector('#settings-btn', { timeout: 5000 });

    // Click the Settings button
    await page.click('#settings-btn');

    // Wait for settings modal to appear
    await page.waitForSelector('#settings-username', { timeout: 5000 });

    // Take screenshot after opening settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_open.png'));

    // Extract some data from the settings modal to verify it opened
    const usernamePlaceholder = await page.$eval('#settings-username', el => el.placeholder);
    const displayNamePlaceholder = await page.$eval('#settings-display-name', el => el.placeholder);

    return {
      success: true,
      action: "Clicked the Settings button in the left sidebar and opened settings modal",
      extractedData: {
        usernameInputPlaceholder: usernamePlaceholder,
        displayNameInputPlaceholder: displayNamePlaceholder
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the Settings button using multiple selector strategies
    // First try ID selector (most reliable)
    await page.waitForSelector('#settings-btn', { state: 'visible' });
    await page.click('#settings-btn');

    // Wait for settings modal to appear using multiple verification methods
    await Promise.all([
      page.waitForSelector('.modal-content'), // Modal container
      page.waitForSelector('#settings-username'), // Settings input field
      page.waitForSelector('text="Account Settings"') // Modal title text
    ]);

    // Take screenshot after opening settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_open.png'));

    // Extract modal title to verify successful opening
    const modalTitle = await page.textContent('.modal-header h2');
    const usernameInput = await page.isVisible('#settings-username');

    return {
      success: true,
      action: "Clicked Settings button and opened settings modal",
      extractedData: {
        modalTitle: modalTitle?.trim(),
        usernameInputVisible: usernameInput
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open settings modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

