import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first suggested account's follow button
    await page.waitForSelector('.account-item:first-child .follow-btn', { timeout: 5000 });

    // Extract account info before clicking
    const accountInfo = await page.$eval('.account-item:first-child', el => ({
      username: el.querySelector('.username')?.textContent || '',
      handle: el.querySelector('.user-handle')?.textContent || '',
      buttonText: el.querySelector('.follow-btn')?.textContent || ''
    }));

    // Click the follow button
    await page.click('.account-item:first-child .follow-btn');

    // Wait for state change and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    // Verify the button state changed
    const newButtonState = await page.$eval('.account-item:first-child .follow-btn', el => el.textContent.trim());
    const isFollowing = newButtonState.toLowerCase().includes('following');

    return {
      success: true,
      action: "Clicked Follow button on first suggested account",
      extractedData: {
        accountUsername: accountInfo.username,
        accountHandle: accountInfo.handle,
        initialButtonState: accountInfo.buttonText,
        finalButtonState: newButtonState,
        isFollowing: isFollowing
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for right sidebar to load and be visible
    await page.waitForSelector('.right-sidebar', { timeout: 10000 });

    // Wait for suggested accounts section to load
    await page.waitForSelector('.suggested-accounts', { timeout: 5000 });

    // Wait for the first account item and its follow button
    const firstAccountSelector = '.account-item:first-child';
    const followButtonSelector = `${firstAccountSelector} .follow-btn`;

    await page.waitForSelector(followButtonSelector, { timeout: 5000 });

    // Extract account info before clicking
    const accountInfo = await page.$eval(firstAccountSelector, (el) => {
      const username = el.querySelector('.username')?.textContent || '';
      const handle = el.querySelector('.user-handle')?.textContent || '';
      return { username, handle };
    });

    // Click the follow button
    await page.click(followButtonSelector);

    // Wait for state change (button might change text/class)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_follow.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_follow.png'));

    return {
      success: true,
      action: "Clicked Follow button on first suggested account",
      extractedData: {
        account: accountInfo,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for right sidebar to load
    await page.waitForSelector('.right-sidebar', { timeout: 10000 });

    // Look for suggested accounts section
    const suggestedAccounts = await page.$$eval('.account-item', items => 
        items.map(item => ({
            username: item.querySelector('.username')?.textContent,
            hasFollowBtn: !!item.querySelector('.follow-btn')
        }))
    );

    // Wait for first account item with follow button
    await page.waitForSelector('.account-item:first-child .follow-btn', { timeout: 5000 });

    // Extract account info before clicking
    const accountInfo = await page.$eval('.account-item:first-child', el => ({
        username: el.querySelector('.username')?.textContent,
        buttonText: el.querySelector('.follow-btn')?.textContent
    }));

    // Click the follow button
    await page.click('.account-item:first-child .follow-btn');

    // Wait for potential state change
    await page.waitForTimeout(1000);

    // Take screenshot after action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    return {
      success: true,
      action: "Clicked Follow button on first suggested account",
      extractedData: {
        accountUsername: accountInfo.username,
        buttonTextBefore: accountInfo.buttonText,
        totalSuggestedAccounts: suggestedAccounts.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
