import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button using ID selector
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Click Appearance tab using attribute selector
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light using select dropdown
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme change by checking if body has light theme class
    const themeClass = await page.$eval('body', el => el.className);
    const isLightTheme = themeClass.includes('light-theme');

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        theme: isLightTheme ? 'light-theme' : 'unknown',
        themeClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button using multiple selector strategies
    const settingsBtn = await page.waitForSelector('#settings-btn, [data-view="settings"]', { timeout: 5000 });
    await settingsBtn.click();
    await page.waitForTimeout(1000);

    // Wait for settings modal and take screenshot
    await page.waitForSelector('.modal-content, [data-modal-type="settings"]', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_open.png'));

    // Click Appearance tab using multiple selector strategies
    const appearanceTab = await page.waitForSelector('.tab-btn[data-tab="appearance"], [data-tab="appearance"]', { timeout: 5000 });
    await appearanceTab.click();
    await page.waitForTimeout(1000);

    // Wait for theme selector and take screenshot
    await page.waitForSelector('#theme-select, select[name="theme"]', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light using multiple selector strategies
    const themeSelect = await page.waitForSelector('#theme-select, select[name="theme"]', { timeout: 5000 });
    await themeSelect.selectOption('light');
    await page.waitForTimeout(1000);

    // Verify theme change by checking if light theme class is applied
    await page.waitForSelector('.light-theme, body.light-theme', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Extract current theme value for verification
    const currentTheme = await page.$eval('#theme-select, select[name="theme"]', select => select.value);
    const isLightTheme = await page.$eval('body', body => body.classList.contains('light-theme'));

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        currentTheme,
        isLightThemeApplied: isLightTheme
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button using CSS selector
    await page.waitForSelector('#settings-btn', { state: 'visible' });
    await page.click('#settings-btn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_clicked.png'));

    // Wait for settings modal and click Appearance tab using CSS selector
    await page.waitForSelector('.tab-btn[data-tab="appearance"]', { state: 'visible' });
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light using select dropdown
    await page.waitForSelector('#theme-select', { state: 'visible' });
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme change by checking selected option
    const selectedTheme = await page.$eval('#theme-select', select => select.value);
    const themeText = await page.$eval('#theme-select option[value="light"]', option => option.textContent);

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        selectedTheme: selectedTheme,
        themeText: themeText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
