import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation items to load
    await page.waitForSelector('.nav-item[data-view="search"]');

    // Verify initial state (Home view should be active)
    const initialActiveView = await page.$eval('.nav-item.active', el => el.dataset.view);
    if (initialActiveView !== 'home') {
      throw new Error(`Expected home view to be active, but found: ${initialActiveView}`);
    }

    // Click on Search navigation item
    await page.click('.nav-item[data-view="search"]');

    // Wait for search view to become active
    await page.waitForSelector('#search-view.active');

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Verify search view is active and extract relevant data
    const searchInputPlaceholder = await page.$eval('#search-input', el => el.placeholder);
    const activeNavItem = await page.$eval('.nav-item.active', el => el.dataset.view);

    return {
      success: true,
      action: "Successfully clicked on Search navigation item and switched to Search view",
      extractedData: {
        activeView: activeNavItem,
        searchInputPlaceholder: searchInputPlaceholder,
        searchViewVisible: await page.isVisible('#search-view.active')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (screenshotError) {
      console.error('Failed to take error screenshot:', screenshotError);
    }

    return {
      success: false,
      action: "Failed to click on Search navigation item and switch to Search view",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation to be ready and click Search item
    await page.waitForSelector('.nav-item[data-view="search"]', { state: 'visible' });
    await page.click('.nav-item[data-view="search"]');

    // Wait for search view to become active
    await page.waitForSelector('#search-view.active', { state: 'visible' });

    // Verify search view is active by checking for search input
    await page.waitForSelector('#search-view #search-input', { state: 'visible' });

    // Take screenshot after switching to search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Extract search view status
    const searchViewActive = await page.$eval('#search-view', el => el.classList.contains('active'));
    const searchInputVisible = await page.$eval('#search-view #search-input', el => el.offsetParent !== null);

    return {
      success: true,
      action: "Clicked on Search navigation item to switch from Home view to Search view",
      extractedData: {
        searchViewActive: searchViewActive,
        searchInputVisible: searchInputVisible,
        currentView: await page.$eval('.nav-item.active', el => el.dataset.view)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-menu', { state: 'visible' });

    // Use multiple selector strategies to find the Search navigation item
    const searchNavItem = await page.$('.nav-item[data-view="search"]');

    // Verify element exists and is clickable
    if (!searchNavItem) {
      throw new Error('Search navigation item not found');
    }

    // Click on the Search navigation item
    await searchNavItem.click();

    // Wait for Search view to become active
    await page.waitForSelector('#search-view.active', { state: 'visible' });
    await page.waitForSelector('#search-input', { state: 'visible' });

    // Take screenshot after successful navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Extract data to verify task completion
    const searchInputPlaceholder = await page.$eval('#search-input', el => el.placeholder);
    const activeView = await page.$eval('.view.active', el => el.id);

    return {
      success: true,
      action: "Clicked on Search navigation item and switched to Search view",
      extractedData: {
        searchInputPlaceholder,
        activeView,
        viewStatus: activeView === 'search-view' ? 'Success' : 'Failed'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
