import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Find all visible like buttons
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click all like buttons in rapid succession
    const clickPromises = likeButtons.map(async (button, index) => {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
      await page.screenshot({ 
        path: path.join(screenshotsDir, `step_2_like_${index + 1}.png`) 
      });
    });

    await Promise.all(clickPromises);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        likedThreadsCount: likeButtons.length,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');

    // Find all visible like buttons using the specified selector
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click all visible like buttons in rapid succession
    const startTime = Date.now();
    for (const button of likeButtons) {
      await button.click();
      // Add minimal delay to ensure proper registration but maintain rapid succession
      await page.waitForTimeout(50);
    }
    const endTime = Date.now();

    // Verify completion within 5 seconds
    const completionTime = endTime - startTime;
    if (completionTime > 5000) {
      throw new Error(`Task took ${completionTime}ms, exceeding 5 second limit`);
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_likes_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_likes_clicked.png'));

    return {
      success: true,
      action: `Clicked like buttons on ${likeButtons.length} visible threads in ${completionTime}ms`,
      extractedData: {
        buttonsClicked: likeButtons.length,
        completionTime: completionTime
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and ensure we're on the home view
    await page.waitForSelector('#threads-feed');
    await page.waitForSelector('.thread-action[data-action="like"]');

    // Get all visible like buttons
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click all like buttons in rapid succession
    for (const button of likeButtons) {
      await button.click();
      // Minimal delay between clicks to ensure rapid succession
      await page.waitForTimeout(100);
    }

    // Take screenshot after clicking all like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    // Extract data to verify completion
    const likeCounts = await page.$$eval('.thread-action[data-action="like"]', buttons => 
      buttons.map(button => {
        const countEl = button.closest('.thread-actions').querySelector('.action-count');
        return {
          liked: button.classList.contains('active'),
          count: countEl ? parseInt(countEl.textContent) : 0
        };
      })
    );

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        totalThreads: likeButtons.length,
        likeStatuses: likeCounts
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
