import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the Activity navigation item to view the activity feed
    await page.waitForSelector('.nav-item[data-view="activity"]');
    await page.click('.nav-item[data-view="activity"]');

    // Wait for the activity feed to load
    await page.waitForSelector('#activity-feed'); // Assuming there's an element with this ID in the activity feed

    // Take a screenshot after clicking the Activity item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_activity_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_activity_feed.png'));

    return {
      success: true,
      action: "Clicked on Activity navigation item to view activity feed",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Activity navigation item to be visible and click it
    await page.waitForSelector('.nav-item[data-view="activity"]', { timeout: 5000 });
    await page.click('.nav-item[data-view="activity"]');

    // Wait for the activity feed to load
    await page.waitForSelector('#activity-feed', { timeout: 5000 });

    // Take a screenshot of the activity feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_activity_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_activity_feed.png'));

    // Extract meaningful data to verify task completion
    const activityCount = await page.evaluate(() => {
      return document.querySelectorAll('.activity-item').length;
    });

    return {
      success: true,
      action: "Clicked on Activity navigation item and viewed activity feed",
      extractedData: { activityCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

