import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open settings
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot after opening settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_open_settings.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_open_settings.png'));

    // Go to Appearance tab
    await page.waitForSelector(".tab-btn[data-tab='appearance']");
    await page.click(".tab-btn[data-tab='appearance']");
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigating to Appearance tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');
    await page.waitForLoadState('networkidle');

    // Take screenshot after changing theme
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_change_theme_light.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_change_theme_light.png'));

    return {
      success: true,
      action: "Changed theme to Light in Appearance settings",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the settings button to open settings
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Click on the Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change the theme to Light
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', { value: 'light' });
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed_to_light.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed_to_light.png'));

    // Verify that the theme has been changed (this can be a more specific check if needed)
    const themeClass = await page.evaluate(() => {
      return document.body.classList.contains('light-theme') ? 'light-theme' : 'not-light-theme';
    });

    return {
      success: true,
      action: "Changed theme to Light in settings",
      extractedData: { themeClass },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

