import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Extract thread text content and metadata
    const threads = await page.$$eval('.thread-item', items => {
      return items.map(item => ({
        text: item.querySelector('.thread-text') ? item.querySelector('.thread-text').innerText : '',
        time: item.querySelector('.thread-time') ? item.querySelector('.thread-time').innerText : '',
        actions: item.querySelector('.thread-action') ? item.querySelector('.thread-action').innerText : ''
      }));
    });

    // Log extracted data for verification
    console.log(threads);

    // Take screenshot of the threads feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_feed.png'));

    return {
      success: true,
      action: "Successfully extracted thread text content and metadata",
      extractedData: threads,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');

    // Extract thread content and metadata
    const threads = await page.evaluate(() => {
      const threadElements = document.querySelectorAll('.thread-item');
      return Array.from(threadElements).map(thread => {
        const text = thread.querySelector('.thread-text') ? thread.querySelector('.thread-text').innerText : '';
        const time = thread.querySelector('.thread-time') ? thread.querySelector('.thread-time').innerText : '';
        const actions = thread.querySelectorAll('.thread-action span');
        const actionTexts = Array.from(actions).map(action => action.innerText);
        return { text, time, actions: actionTexts };
      });
    });

    // Take screenshot of the threads feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_feed.png'));

    return {
      success: true,
      action: "Successfully extracted thread contents and metadata",
      extractedData: { threads },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

