import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts section to load
    await page.waitForSelector('.suggested-accounts');

    // Click the Follow button on the first suggested account
    const followButton = await page.$('.suggested-accounts .account-item:first-child .follow-btn');

    if (followButton) {
      await followButton.click();
      await page.waitForTimeout(1000); // Wait for any follow confirmation or state change

      // Take a screenshot after clicking the follow button
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

      return {
        success: true,
        action: "Clicked Follow button on the first suggested account",
        extractedData: {
          followedAccount: await followButton.evaluate(el => el.closest('.account-item').querySelector('.username').textContent)
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error("Follow button not found");
    }

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
