import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Settings button to be visible and click it
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');

    // Take screenshot after clicking the Settings button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Verify if the settings modal is opened (you might need to adjust the selector based on the actual modal)
    const isModalVisible = await page.isVisible('.settings-modal'); // Assuming the modal has a class 'settings-modal'

    return {
      success: true,
      action: "Clicked the Settings button to open settings modal",
      extractedData: { modalOpened: isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

