import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Settings button to be visible and click it
    await page.waitForSelector('#settings-btn', { timeout: 5000 });
    await page.click('#settings-btn');

    // Take screenshot after clicking settings button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_opened.png'));

    // Verify that the settings modal is opened by checking for a specific element in the modal
    const settingsModalVisible = await page.isVisible('.settings-modal'); // Assuming .settings-modal is the class for the modal
    if (!settingsModalVisible) {
      throw new Error("Settings modal did not open as expected.");
    }

    return {
      success: true,
      action: "Clicked the Settings button and opened the settings modal.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
