import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread text area to be visible
    await page.waitForSelector('#thread-text');

    // Type the text into the thread text area
    await page.fill('#thread-text', 'Testing character count functionality');

    // Take a screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Wait for the character count to update
    await page.waitForSelector('#char-count');

    // Extract the character count
    const charCountText = await page.textContent('#char-count');

    // Verify the character count updates
    const charCount = charCountText.split('/')[0].trim(); // Get the count before the slash
    const expectedCount = '36'; // Length of 'Testing character count functionality'

    // Take a screenshot of the character count
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_character_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_character_count.png'));

    return {
      success: charCount === expectedCount,
      action: "Typed text and verified character count updates",
      extractedData: { charCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

