import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread input field and type 'Hello World!'
    await page.waitForSelector('#thread-text');
    await page.fill('#thread-text', 'Hello World!');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typing.png'));

    // Click the Post button
    await page.waitForSelector('#post-thread');
    await page.click('#post-thread');

    // Take screenshot after posting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_posted.png'));

    // Verify the post was successful by checking for the presence of the new thread in the feed
    await page.waitForSelector('.threads-feed'); // Wait for threads to load
    const threadContent = await page.locator('.threads-feed').innerText();
    const success = threadContent.includes('Hello World!');

    return {
      success,
      action: "Posted 'Hello World!' in the thread",
      extractedData: { threadContent },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

