import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggested accounts section to load
    await page.waitForSelector('.account-item');

    // Click the Follow button on the first suggested account
    const followButtonSelector = '.account-item:first-child .follow-btn';
    await page.waitForSelector(followButtonSelector);
    await page.click(followButtonSelector);

    // Take a screenshot after clicking the Follow button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    // Extract meaningful data to verify task completion
    const firstAccountUserName = await page.$eval('.account-item:first-child .username', el => el.innerText);
    const firstAccountHandle = await page.$eval('.account-item:first-child .user-handle', el => el.innerText);

    return {
      success: true,
      action: `Clicked Follow button for ${firstAccountUserName} (${firstAccountHandle})`,
      extractedData: {
        userName: firstAccountUserName,
        userHandle: firstAccountHandle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
