import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Extract thread text content and metadata
    const threads = await page.evaluate(() => {
      const threadElements = document.querySelectorAll('.thread-item'); // Assuming thread items have a class 'thread-item'
      return Array.from(threadElements).map(thread => {
        const textContent = thread.querySelector('.thread-text')?.innerText || '';
        const timestamp = thread.querySelector('.thread-time')?.innerText || '';
        const actions = thread.querySelector('.thread-action')?.innerText || '';
        return { textContent, timestamp, actions };
      });
    });

    // Take screenshot of the threads feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_feed.png'));

    return {
      success: true,
      action: "Successfully extracted thread text content and metadata",
      extractedData: { threads },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Extract all thread text content
    const threadContents = await page.$$eval('.thread-content', threads => 
      threads.map(thread => thread.textContent.trim())
    );

    // Extract thread metadata: time and actions
    const threadMetadata = await page.$$eval('.thread-time, .thread-action span', elements => 
      elements.map(element => element.textContent.trim())
    );

    // Combine extracted data into a structured object
    const extractedData = {
      threads: threadContents,
      metadata: threadMetadata
    };

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_extraction.png'));

    return {
      success: true,
      action: "Extracted thread content and metadata",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

