import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Search navigation item to be visible and clickable
    await page.waitForSelector('.nav-item[data-view="search"]', { state: 'visible' });

    // Click on the Search navigation item
    await page.click('.nav-item[data-view="search"]');

    // Wait for the Search view to be visible
    await page.waitForSelector('#search-view', { state: 'visible' });

    // Take a screenshot after switching to Search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Extract data to verify task completion
    const isSearchViewActive = await page.evaluate(() => {
      return document.querySelector('.view.active').id === 'search-view';
    });

    return {
      success: true,
      action: "Switched from Home view to Search view",
      extractedData: { isSearchViewActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
