import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Select all visible like buttons
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click each like button rapidly
    for (const button of likeButtons) {
      await button.click();
      // Adding a slight delay to ensure rapid succession but within 5 seconds
      await page.waitForTimeout(100); // Adjust this value if needed
    }

    // Take a screenshot after clicking like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_like_buttons_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_like_buttons_clicked.png'));

    // Extracting the number of likes for verification
    const likesData = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.thread-action[data-action="like"]')).map(button => {
        return {
          likesCount: button.closest('.thread-item').querySelector('.likes-count')?.textContent || '0',
        };
      });
    });

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: { likesData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
