import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread to load
    await page.waitForSelector('.threads-feed .thread-item:first-child');

    // Click the like button on the first thread
    const likeButtonSelector = '.threads-feed .thread-item:first-child .thread-action[data-action="like"]';
    await page.waitForSelector(likeButtonSelector);
    await page.click(likeButtonSelector);

    // Take a screenshot after clicking the like button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_like_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_like_clicked.png'));

    // Extract the number of likes after action
    const likesCount = await page.$eval(likeButtonSelector, button => button.textContent);

    return {
      success: true,
      action: "Clicked like button on the first thread",
      extractedData: { likesCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
