import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread to be visible
    await page.waitForSelector('.threads-feed .thread-item:first-child', { timeout: 5000 });

    // Click the reply button on the first thread
    await page.click('.threads-feed .thread-item:first-child .thread-action[data-action="reply"]');

    // Take screenshot after clicking reply
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reply_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reply_modal_opened.png'));

    // Verify that the reply modal is opened
    const isModalVisible = await page.isVisible('.reply-modal'); // Assuming '.reply-modal' is the selector for the reply modal
    const extractedData = { modalOpened: isModalVisible };

    return {
      success: true,
      action: "Clicked the reply button on the first thread, opening the reply modal.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
