import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Type text into the thread input field
    const threadText = 'Testing character count functionality';
    await page.waitForSelector('#thread-text');
    await page.fill('#thread-text', threadText);

    // Verify character count updates
    await page.waitForSelector('#char-count');
    const charCountText = await page.innerText('#char-count');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Extract meaningful data to verify task completion
    const charCount = charCountText.split('/')[0].trim(); // Get the character count before the slash
    const expectedCount = threadText.length.toString();

    return {
      success: charCount === expectedCount,
      action: "Typed text and verified character count updates",
      extractedData: { charCount, expectedCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

