import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Settings button to be visible and click it
    await page.waitForSelector('#settings-btn', { state: 'visible' });
    await page.click('#settings-btn');

    // Take screenshot after clicking the Settings button
    await page.waitForSelector('.settings-modal', { state: 'visible' }); // Assuming the modal has a class 'settings-modal'
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal_opened.png'));

    return {
      success: true,
      action: "Clicked the Settings button and opened the settings modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

