import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('#threads-feed');

    // Extract thread text content and metadata
    const threads = await page.$$eval('.thread-item', items => {
      return items.map(item => {
        const textContent = item.querySelector('.thread-text') ? item.querySelector('.thread-text').innerText : '';
        const timeContent = item.querySelector('.thread-time') ? item.querySelector('.thread-time').innerText : '';
        return { text: textContent, time: timeContent };
      });
    });

    // Take screenshot of the threads feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_feed.png'));

    // Return extracted data
    return {
      success: true,
      action: "Successfully extracted thread text content and metadata",
      extractedData: { threads },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

