import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to be loaded
    await page.waitForSelector('#threads-feed', { state: 'visible' });

    // Get all visible like buttons within the threads
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click each like button rapidly
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // short delay to mimic rapid clicking
    }

    // Take a screenshot after liking the threads
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    // Verify if the like actions were successful (this can vary based on how the application updates)
    const likesAfter = await Promise.all(likeButtons.map(async (button) => {
      const parentThread = await button.evaluate(el => el.closest('.thread-item'));
      return await parentThread.evaluate(el => el.querySelector('.like-count').innerText);
    }));

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: { likesCount: likesAfter },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
