import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the Activity navigation item to view activity feed
    await page.waitForSelector('.nav-item[data-view="activity"]');
    await page.click('.nav-item[data-view="activity"]');

    // Wait for the activity feed to load
    await page.waitForSelector('.main-content'); // Assuming the main content updates
    await page.waitForTimeout(1000); // Additional wait for dynamic content

    // Take a screenshot after clicking the Activity item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_activity_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_activity_feed.png'));

    // Extract some meaningful data to verify task completion
    const activityFeedVisible = await page.isVisible('.main-content');

    return {
      success: true,
      action: "Clicked on Activity navigation item and viewed activity feed",
      extractedData: { activityFeedVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

