import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search view to be visible
    await page.waitForSelector('#search-view');

    // Click on the 'Hashtags' tab to switch search category
    await page.click("button.tab-btn[data-tab='hashtags']");

    // Take a screenshot after clicking the tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hashtags_tab_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hashtags_tab_clicked.png'));

    // Verify if the hashtags tab is active
    const isActive = await page.evaluate(() => {
      return document.querySelector("button.tab-btn[data-tab='hashtags']").classList.contains('active');
    });

    return {
      success: true,
      action: "Clicked on 'Hashtags' tab to switch search category",
      extractedData: { isActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

