import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first thread item to be visible
    await page.waitForSelector('.thread-item:first-child .thread-action[data-action="bookmark"]');

    // Click the bookmark button on the first thread
    await page.click('.thread-item:first-child .thread-action[data-action="bookmark"]');

    // Take a screenshot after clicking the bookmark button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmark_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmark_clicked.png'));

    // Extract confirmation or state change data if available
    const isBookmarked = await page.evaluate(() => {
      const firstThread = document.querySelector('.thread-item:first-child');
      return firstThread ? firstThread.querySelector('.thread-action[data-action="bookmark"]').classList.contains('bookmarked') : false;
    });

    return {
      success: true,
      action: "Clicked the bookmark button on the first thread",
      extractedData: { isBookmarked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

