import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the threads feed to load
    await page.waitForSelector('.threads-feed');

    // Extract thread text content and metadata
    const threads = await page.$$eval('.thread-item', items => {
      return items.map(item => ({
        text: item.querySelector('.thread-text') ? item.querySelector('.thread-text').innerText : '',
        time: item.querySelector('.thread-time') ? item.querySelector('.thread-time').innerText : '',
        actions: item.querySelector('.thread-action') ? item.querySelector('.thread-action').innerText : ''
      }));
    });

    // Log extracted threads data
    console.log(threads);

    // Take screenshot of the threads feed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_feed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_feed.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the feed",
      extractedData: { threads },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');

    // Extract thread text content and metadata
    const threadElements = await page.$$('.threads-feed .thread-item'); // Using class selector for thread items
    const threadsData = [];

    for (const thread of threadElements) {
      const content = await thread.$eval('.thread-text', el => el.innerText); // Extracting thread text
      const time = await thread.$eval('.thread-time', el => el.innerText); // Extracting thread time
      const author = await thread.$eval('.thread-username', el => el.innerText); // Extracting author username

      threadsData.push({ content, time, author });
    }

    // Log extracted data
    console.log(threadsData);

    // Take final screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Successfully extracted thread text and metadata",
      extractedData: threadsData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

