import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Rapidly open and close the reply modal 10 times
    for (let i = 0; i < 10; i++) {
      // Open the reply modal
      await page.waitForSelector(".thread-action[data-action='reply']");
      await page.click(".thread-action[data-action='reply']");
      await page.waitForTimeout(500); // Wait for the modal to open

      // Take screenshot after opening the modal
      await page.screenshot({ path: path.join(screenshotsDir, `step_${i + 2}_modal_open.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${i + 2}_modal_open.png`));

      // Close the reply modal
      await page.waitForSelector("#close-reply-modal");
      await page.click("#close-reply-modal");
      await page.waitForTimeout(500); // Wait for the modal to close

      // Take screenshot after closing the modal
      await page.screenshot({ path: path.join(screenshotsDir, `step_${i + 3}_modal_close.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${i + 3}_modal_close.png`));
    }

    return {
      success: true,
      action: "Rapidly opened and closed the reply modal 10 times",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

